package entity

import (
	"github.com/google/uuid"
	"github.com/phanirithvij/fate/f8/xfs"
)

// Base a base entity model
type Base struct {
	// ID entity id autogenerated
	UID     string
	buckets []xfs.Bucket
	// Register func(db *gorm.DB) error `gorm:"-"`
}

// NewBase a new base
func NewBase() *Base {
	id := uuid.New()
	return &Base{UID: id.String()}
}

func (b *Base) String() string {
	return "Base@" + b.UID
}
